import React, { Component } from 'react';
import { Redirect } from 'react-router-dom';
import { connect } from 'react-redux';
import { formatQuestion } from '../utils/_DATA'
import {handleAnswerQuestion} from '../actions/questions'




class QuestionPage extends Component {

    isAnswered = function () {
        const { authedUser, questions, question, users } = this.props
        const questionId = this.props.id
        const authedUserObject = users[authedUser]
        // const id = this.props.match.params['id']
        // const question = questions[id]

        return Object.keys(authedUserObject.answers).filter(id => id === questionId)

    }

    handleAnswer = (e) => {
        e.preventDefault()

        const { dispatch, question, authedUser } = this.props

        dispatch(handleAnswerQuestion({
            id: question.id,
            answer: question.answer,
            authedUser
        }))
    }

    render() {
        const { questions, question, users, authedUser, id } = this.props;
        // const id = this.props.match.params['id']
        // const question = questions[id]
        // const users = this.props.users
        const authedUserObject = users[authedUser]

        console.log('authedUserObject', authedUserObject.answers[id])

        return (
            <div className="question">
                <img
                    src={users[question.author].avatarURL ? users[question.author].avatarURL : 'https://oshinon.com/wp-content/uploads/2018/12/man.png'}
                    alt={`Avatar of ${users[question.author].name}`}
                    className='avatar'
                />
                <div className='question-info'>
                    <div>
                        <span>{users[question.author].name}</span>
                    </div>
                    <span className='center'>Would You Rather...</span>
                    {this.isAnswered().length <= 0 ?
                        <form>
                            <div>
                                <input type="radio"
                                    name="asnwerOption" value="optionOne" checked />
                                <label>{question.optionOne.text}</label><br />
                                <input type="radio"
                                    name="asnwerOption" value="optionTwo" />
                                <label for="contactChoice2">{question.optionTwo.text}</label><br />
                            </div>
                            <button value='' className='btn center' onClick={this.handleAnswer} >
                            submit my answer
                            </button>
                        </form>
                        : <div>
                            {authedUserObject.answers[id] === 'optionOne' ? <p className='bg-green'>{question.optionOne.text}</p> : <p>{question.optionOne.text}</p>}
                            {authedUserObject.answers[id] === 'optionTwo' ? <p className='bg-green'>{question.optionTwo.text}</p> : <p>{question.optionTwo.text}</p>}
                        </div>}
                </div>
            </div>
        );
    }
}


function mapStateToProps({ authedUser, questions, users }, props) {

    const id = props.match.params['id']
    const question = questions[id]
    question['optionOneText'] = question.optionOne.text
    question['optionTwoText'] = question.optionTwo.text
    authedUser = 'johndoe'


    return {
        id,
        users,
        questions,
        authedUser,
        question: question
            ? formatQuestion(question)
            : null
    }
}

export default connect(mapStateToProps)(QuestionPage);
