import React, { Component } from 'react'
import { connect } from 'react-redux'
import Question from './Question'

class Dashboard extends Component {
    state = {
        questionsToShow: [],
    }
    constructor(props) {
        super(props);
        this.state = {
            questionsToShow: [],
        }
     }

    componentDidMount(){
        const questions = this.props.questions
        this.setState(function () {
            return {
                questionsToShow: this.props.questionsIds.filter(id => questions[id].optionOne.votes.includes(this.props.authedUser) || questions[id].optionTwo.votes.includes(this.props.authedUser))
            }
        })

        console.log('questions' , questions)
        console.log('questionIds',this.props.questionsIds)
        console.log('authedUser', this.props.authedUser)
        console.log('questionsToShow', this.state.questionsToShow)
    }

    handleAnswerdClicked = function () {
        const questions = this.props.questions
        this.setState(function () {
            return {
                questionsToShow: this.props.questionsIds.filter(id => questions[id].optionOne.votes.includes(this.props.authedUser) || questions[id].optionTwo.votes.includes(this.props.authedUser))
            }
        })
        console.log('authedUser', this.props.authedUser)
        console.log('questionsToShow', this.state.questionsToShow)
    }

    handleUnanswerdClicked = function () {
        const questions = this.props.questions
        this.setState(function () {
            return {
                questionsToShow: this.props.questionsIds.filter(id => !this.state.questionsToShow.includes(id))
            }
        })
        console.log('authedUser', this.props.authedUser)
        console.log('questionsToShow', this.state.questionsToShow)
    }

    render() {
        const { authedUser, questionsIds } = this.props;
        console.log(this.props)

        return (
            <div>
                <h3 className='center'>Your Timeline</h3>
                <div className='center'>
                    <button
                        onClick={(event) => this.handleUnanswerdClicked(false)}>
                        Unanswerd questions
                    </button>
                    <button
                        onClick={(event) => this.handleAnswerdClicked(false)}>
                        Answerd questions
                    </button>
                </div>

                <ul className='dashboard-list'>
                    {this.state.questionsToShow.map((id) => (
                        <li key={id}>
                            <Question id={id} />
                        </li>
                    ))}
                </ul>
            </div>
        )
    }
}

function mapStateToProps({ authedUser, questions, users }) {
    return {
        questionsIds: Object.keys(questions),
        questions,
        authedUser: 'johndoe',
        users,
    }
}

export default connect(mapStateToProps)(Dashboard)
