import React, { Component } from 'react';
import { connect } from 'react-redux';
import Dropdown from 'react-dropdown'
import 'react-dropdown/style.css'
import { setAuthedUser, logout } from '../actions/authedUser'
import { withRouter } from 'react-router-dom'

class Login extends Component {
    state = {
        redirectToReferrer: false
    }

    render() {
        const AuthButton = withRouter(({ history }) => (
            authantication.isAuthenticated ? (
                <p>
                    Welcome! <button onClick={() => {
                        authantication.signout(() => history.push('/'))
                    }}>Sign out</button>
                </p>
            ) : (
                    <p>You are not logged in.</p>
                )
        ))


        let handleLogin = function(value)  {
            this.props.dispatch(setAuthedUser(value.value))
            authantication.authenticate(() => {
                this.setState(() => ({
                    redirectToReferrer: true
                }))
            })

        }



        const authantication = {
            isAuthenticated: this.props.authedUser ? true : false,
            authenticate(cb) {
                this.isAuthenticated = true
                setTimeout(cb, 100)
            },
            signout(cb) {
                this.props.dispatch(logout())
                this.isAuthenticated = false
                setTimeout(cb, 100)
            }
        }


        return (
            <div>
                <Dropdown
                    options={Object.values(this.props.users).map(user => {
                        return {
                            value: user.id,
                            label: user.name
                        }
                    })}
                    onChange={this.handleLogin}
                    placeholder="Select an option"
                />
            </div>
        )
    }

}

function mapStateToProps({ users, authedUser }) {
    return {
        users,
        authedUser
    };
}

export default connect(mapStateToProps)(Login);
