import React, { Component } from 'react'
import { connect } from 'react-redux'
import Question from './Question'

class Dashboard extends Component {
    state = {
        questionsToShow : [],
    }
  handleFilterClicked = function() {
    this.setState(function() {
        const questions =this.props.questions
      return {
        questionsToShow: this.props.questionsIds.filter(id => questions[id].optionOne.votes.includes(this.props.authedUser) ||  questions[id].optionTwo.votes.includes(this.props.authedUser))
      }
    });
  }

    render() {
        const { authedUser, questionsIds } = this.props;
        console.log(this.props)

        return (
            <div>
                <h3 className='center'>Your Timeline</h3>
                <div className='center'>
                    <button>
                        answerd questions
                    </button>
                    <button
                     onClick={(event) => this.handleFilterClicked(false)}>
                        unanswerd questions
                    </button>
                </div>
                
                <ul className='dashboard-list'>
                    {this.state.questionsToShow.map((id) => (
                        <li key={id}>
                            <Question id={id} />
                        </li>
                    ))}
                </ul>
            </div>
        )
    }
}

function mapStateToProps({ authedUser, questions, users }) {
    return {
        questionsIds: Object.keys(questions),
        questions,
        authedUser,
        users,
    }
}

export default connect(mapStateToProps)(Dashboard)
