import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import * as actions from '../actions/questions';
import { formatQuestion } from '../utils/_DATA'

class Question extends Component {

  render() {
    const question = this.props.question
    const users = this.props.users
    console.log('text = ' + question.optionOne.text)
    return (
      <div className="question">
        <img
          src={users[question.author].avatarURL}
          alt={`Avatar of ${users[question.author].name}`}
          className='avatar'
        />
        <div className='question-info'>
          <div>
            <span>{users[question.author].name}</span>
          </div>
          <span className='center'>Would You Rather...</span>
          <form>
            <div>
              <input type="radio"
                name="asnwerOption" value="optionOne" />
              <label>{question.optionOne.text}</label><br />
              <input type="radio"
                name="asnwerOption" value="optionTwo" />
              <label for="contactChoice2">{question.optionTwo.text}</label><br />
            </div>
            <input Stype='submit' value='submit my answer' />
          </form>
        </div>
      </div>
    )
  }
}

function mapStateToProps({ authedUser, users, questions }, { id }) {
  const question = questions[id]
  question['optionOneText'] = question.optionOne.text
  question['optionTwoText'] = question.optionTwo.text

  return {
    questions,
    authedUser,
    users,
    question: question
      ? formatQuestion(question)
      : null
  };
}

export default connect(mapStateToProps, actions)(Question);
