import React, { Component } from 'react'
import { connect } from 'react-redux'
import 'react-dropdown/style.css'

class Leaderboard extends Component {
    render() {

        const { users } = this.props
        const usersArray = Object.values(users)
        const sortedUsers = usersArray.sort((userA, userB) =>  userA.answers.length + userA.questions.length > userB.answers.length + userB.questions.length ? 1 : -1 )
        console.log('usersArray',usersArray)
        return (
            <div>
                <ul>
                    {sortedUsers.map((user) => (
                        <li key={user.id}>
                            <h2> {usersArray[user.id].name}</h2>
                            <p>{usersArray[user.id].questions.length}</p>
                            <p>{usersArray[user.id].answers.length}</p>
                        </li>
                    ))}

                </ul>
            </div>
        )
    }
}
function mapStateToProps({ users, questions }) {
    return {
        users,
        questions
    }
}

export default connect(mapStateToProps)(Leaderboard)
