import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import * as actions from '../actions/questions';
import { formatQuestion } from '../utils/_DATA'

class Question extends Component {

  render() {
    const question = this.props.question
    const users = this.props.users
    console.log(question.optionOne.text)
    return (
      <div>
        <p>{question.author}</p>
        <span>Would You Rather...</span>
        <div>
          <p>{question.optionOne.text}</p>
        </div>
        <div>
          {question.optionTwo.text}
        </div>
      </div>
    )
  }
}

function mapStateToProps({ authedUser, users, questions },{id}) {
  const question = questions[id]
  console.log('id = ')
  console.log(questions[id].optionOne.text)
  return {
    questions,
    authedUser,
    users,
    question: question
      ? formatQuestion(question, question.optionOne.text, question.optionTwo.text, question.author )
      : null
  };
}

export default connect(mapStateToProps, actions)(Question);
