import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import * as actions from '../actions/questions';
import { formatQuestion } from '../utils/_DATA'

class Question extends Component {

  render() {
    const question = this.props.question

    return (
      <Link to={`/questions/${question.id}`}>

        <span>Would You Rather...</span>
        <div className='option'>
          {question.optionOneText}
        </div>
        <div className='option'>
          {question.optionTwoText}
        </div>
        
      </Link>
    );
  }
}

function mapStateToProps({ authedUser, users, questions },{id}) {
  console.log("the id of the question is = "+questions[id])
  const question = questions[id]
  const optionOneText = "op1"
  const optionTwoText = "op2" 
  const author = "author"
  return {
    questions,
    authedUser,
    users,
    question: question
    ? formatQuestion(question, optionOneText, optionTwoText, author )
    : null
  };
}

export default connect(mapStateToProps, actions)(Question);
